import React from 'react';
import './App.css';

function Hello(props){
    const style={
        backgroundColor:'black',
        fontColor:'aqua',
        fontSize:24,
        padding:'1rem'
    }
    const name='react';
    return (
        <>
            <div> {props.name}님, 안녕하세요</div>
            <div className="gray-box" style={style}>{name}</div>
        </>
    )
}

export default Hello;
